import io from 'socket.io-client';
import { updateCustomers } from '../../actions';

const socketMiddleware = (url) => {
	return store => {
		let socket = io(url);
		socket.on('CUSTOMER_NAMES', data => {
			store.dispatch(updateCustomers(data))	
		});
	}
	return next => action => {
        	switch(action.type){
			default: break;
		}
	}
	return next( action );
}

export {socketMiddleware};
