import { initialTransactionsState } from './initialState';

const transactionsReducer = (state = initialTransactionsState, action ) => {
	switch( action.type ) {
		case 'GET_ALL_TRANSACTIONS':
			return Object.freeze({
				...state,
				data: [
					...action.payload
				]
			});
			break;
		default: return state
	}
}

export {transactionsReducer};
