import React, {Component} from 'react';
import { ComboBox } from '@progress/kendo-react-dropdowns';
import {ClientTable, ProductTable} from './Grids';
import io from 'socket.io-client';

import '@progress/kendo-theme-default/dist/all.css'

const url = "http://localhost:8000"

var storeState = {
	item: undefined, 
	open: true, 
	data: [], 
	grid: <ProductTable/>,
	comboBox: <ComboBox/>
}

const StoreSelector = () => {
	const handleChange = (event) => {
		storeState.item = event.target.value
		storeState.comboBox = <ProductTable columnData={storeState.data}/>
	}
	function middleware() {
		let socket = io( url )
		socket.on('ALL_TRANSACTIONS', transaction_data => {
			storeState.data =  transaction_data
			console.log( storeState.data )
			storeState.grid = <ProductTable columnData={transaction_data}/>
		});
		socket.on('STORE_ADDRESSES', data => {
			storeState.comboBox = <ComboBox data={data} onChange={handleChange}/>	
		});
		return (
		<div className="Selector">
		<h1> Επιλογή Καταστήματος </h1>
		{storeState.comboBox}
		{storeState.grid}
		</div>
		);
	}
	return middleware()
	return (
		<div className="Selector">
		<h1> Επιλογή Καταστήματος </h1>
		{storeState.comboBox}
		{storeState.grid}
		</div>
	);
}

const ClientSelector = () => {
	const [state, setState] = React.useState({
		item: undefined, 
		open: true, 
		data: [], 
		socket: io( url ), 
		grid: <ClientTable/>,
		comboBox: <ComboBox/>
	});
	const handleChange = (event) => {
		setState({ ...state, open: true })
		setState({ ...state, item: event.target.value })	
		state.socket.emit( 'SELECT_CUSTOMER', event.target.value )
	}
	state.socket.on('CUSTOMER_NAMES', data => {
		setState({ ...state, comboBox: ( <ComboBox data={data} onChange={handleChange}/> ) })
	});
	state.socket.on('CUSTOMER_DATA', data => {
		state.data = data;
		setState({ ...state, grid: ( <ClientTable columnData={state.data}/> ) })
	});
	return (
		<div className="Selector">
		<h1> Επιλογή Πελάτη </h1>
		{state.comboBox}
		{state.grid}
		</div>
	);
}

export { StoreSelector, ClientSelector };
