import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles({
	table: {
		minWidth: 650,
	},
});

function createData( amount, dateTime, payment_method ) {
	return { amount, dateTime, payment_method };
}

const TransactionTable = (data) => {
	const classes = useStyles();
	var json = Object.values( data )[ 0 ]
	const rows = []
	for( var i = 0; i < json.length; i++ ) {
		let packet = createData( Object.values( json[ i ] ) )
		console.log( packet )
		rows.push( packet )
	}
	return (
		<TableContainer component={Paper}>
		<Table className={classes.table} aria-label="simple table">
		<TableHead>
		<TableRow>
		<TableCell align="left">Amount</TableCell>
		<TableCell align="left">Date-Time</TableCell>
		<TableCell align="left">Payment Method</TableCell>
		</TableRow>
		</TableHead>
		<TableBody>
		{rows.map((dict) => (
			<TableRow key={dict.amount}>
			<TableCell component="th" scope="row">
			{dict.amount}
			</TableCell>
			<TableCell align="right">{dict.dateTime}</TableCell>
			<TableCell align="right">{dict.payment_method}</TableCell>
			</TableRow>
		))}
		</TableBody>
		</Table>
		</TableContainer>
	);
}

export {TransactionTable};
