import React, {Component} from 'react';
import { ComboBox } from '@progress/kendo-react-dropdowns';
import {ClientTable, ProductTable} from './Grids';
import io from 'socket.io-client';

import '@progress/kendo-theme-default/dist/all.css'

const url = "http://localhost:8000"

const StoreSelector = () => {
	const [state, setState] = React.useState({
		item: undefined, 
		open: true, 
		transaction_data: [], 
		payment_methods: [ 'Credit Card', 'Cash' ],
		names: [],
		socket: io( url )
	});
	const handleName = (event) => {
		setState({ ...state, item: event.target.value })
	}
	const handlePayment = (event) => {
		var payment = 0
		switch( event.target.value ) {
			case 'Credit Card':
				payment = 0
				break
			case 'Cash':
				payment = 1
				break
		}
		state.socket.emit('SELECT_PAYMENT_METHOD', payment)
	}
	state.socket.on('TRANSACTION_DATA', data => {
		setState({ ...state, transaction_data: data })	
	});
	state.socket.on('STORE_ADDRESSES', data => {
		setState({ ...state, names: data })	
	});
	return (
		<div className="Selector">
		<h1> Επιλογή Καταστήματος </h1>
		<ComboBox data={state.names} onChange={handleName}/>
		<ComboBox data={state.payment_methods} onChange={handlePayment}/>
		<ProductTable columnData={state.transaction_data}/>
		</div>
	);
}

const ClientSelector = () => {
	const [state, setState] = React.useState({
		item: undefined, 
		open: true, 
		data: [], 
		socket: io( url ),
		grid: <ClientTable/>,
		comboBox: <ComboBox/>
	});
	const handleChange = (event) => {
		setState({ ...state, open: true })
		setState({ ...state, item: event.target.value })	
		state.socket.emit( 'SELECT_CUSTOMER', event.target.value )
	}
	state.socket.on('CUSTOMER_NAMES', data => {
		setState({ ...state, comboBox: ( <ComboBox data={data} onChange={handleChange}/> ) })
	});
	state.socket.on('CUSTOMER_DATA', data => {
		state.data = data;
		setState({ ...state, grid: ( <ClientTable columnData={state.data}/> ) })
	});
	return (
		<div className="Selector">
		<h1> Επιλογή Πελάτη </h1>
		{state.comboBox}
		{state.grid}
		</div>
	);
}

export { StoreSelector, ClientSelector };
