import * as React from 'react';
import { PluginHost, PluginPositionFn } from '@devexpress/dx-core';

/** @internal */
export interface TemplateHostInterface {
  params(...args): any;
  templates(...args): any;
}

/** @internal */
export const PluginHostContext = React.createContext<PluginHost | null>(null);
/** @internal */
export const PositionContext = React.createContext<PluginPositionFn>(() => []);
/** @internal */
export const TemplateHostContext = React.createContext<TemplateHostInterface | null>(null);
import * as React from 'react';
import { PluginPositionFn } from '@devexpress/dx-core';
import { PositionContext } from './contexts';

type MemoizedPluginPositionFn =
  (index: number, positionContext: PluginPositionFn) => PluginPositionFn;

type PluginIndexerProps = {
  children: React.ReactNode;
};

/** @internal */
export class PluginIndexer extends React.PureComponent<PluginIndexerProps> {
  indexes: { [index: number]: PluginPositionFn } = {};
  memoize: MemoizedPluginPositionFn = (index, positionContext) => {
    if (this.indexes[index]) return this.indexes[index];

    const fn: PluginPositionFn = () => {
      const calculatedPosition = positionContext();
      return [...calculatedPosition, index];
    };
    this.indexes[index] = fn;

    return fn;
  }
  render() {
    const { children } = this.props;
    return (
      <PositionContext.Consumer>
        {positionContext => (
          React.Children.map(children, (child: any, index: number) => {
            if (!child || !child.type) return child;
            const childPosition = this.memoize(index, positionContext);

            return (
              <PositionContext.Provider key={String(index)} value={childPosition}>
                {child}
              </PositionContext.Provider>
            );
          })
        )}
      </PositionContext.Consumer>
    );
  }
}
/** @internal */
export const PLUGIN_HOST_CONTEXT = 'dxcore_pluginHost_context';
/** @internal */
export const POSITION_CONTEXT = 'dxcore_position_context';
/** @internal */
export const TEMPLATE_HOST_CONTEXT = 'dxcore_templateHost_context';

/** @internal */
export const RERENDER_TEMPLATE_EVENT = Symbol('rerenderTemplate');
/** @internal */
export const RERENDER_TEMPLATE_SCOPE_EVENT = Symbol('rerenderTemplateScope');
/** @internal */
export const UPDATE_CONNECTION_EVENT = Symbol('updateConnection');
import * as React from 'react';
import { PluginHostContext, PositionContext } from '../plugin-based/contexts';
import { PLUGIN_HOST_CONTEXT, POSITION_CONTEXT } from '../plugin-based/constants';

/** @internal */
export const withContext = (Context, name) => Component => props => (
  <Context.Consumer>
    {context => (
      <Component {...props} {...{ [name]: context }} />
    )}
  </Context.Consumer>
);

/** @internal */
export const withHostAndPosition = Component => withContext(
  PluginHostContext,
  PLUGIN_HOST_CONTEXT,
)(withContext(PositionContext, POSITION_CONTEXT)(Component));
import * as React from 'react';
import { InnerPlugin } from '@devexpress/dx-core';
import { PluginIndexer } from './plugin-indexer';
import { PLUGIN_HOST_CONTEXT, POSITION_CONTEXT } from './constants';
import { withHostAndPosition } from '../utils/with-props-from-context';
import { PluginContextProps } from './plugin-context-prop-types';
import { IDependency } from '../types';

export interface PluginProps {
  /** React elements that expose the plugin's state and actions and render the plugin's UI. */
  children: React.ReactNode;
  name?: string;
  dependencies?: IDependency[];
}

/** @internal */
export class PluginBase extends React.PureComponent<PluginProps & PluginContextProps> {
  plugin!: InnerPlugin;

  componentDidMount() {
    const { [PLUGIN_HOST_CONTEXT]: pluginHost, [POSITION_CONTEXT]: position } = this.props;
    const { name, dependencies } = this.props;
    this.plugin = {
      position,
      name,
      dependencies,
      container: true,
    };
    pluginHost.registerPlugin(this.plugin);
    pluginHost.ensureDependencies();
  }

  componentDidUpdate() {
    const { [PLUGIN_HOST_CONTEXT]: pluginHost } = this.props;
    pluginHost.ensureDependencies();
  }

  componentWillUnmount() {
    const { [PLUGIN_HOST_CONTEXT]: pluginHost } = this.props;
    pluginHost.unregisterPlugin(this.plugin);
  }

  render() {
    const { children } = this.props;
    return (
      <PluginIndexer>
        {children}
      </PluginIndexer>
    );
  }
}

export const Plugin: React.ComponentType<PluginProps> = withHostAndPosition(PluginBase);
import * as React from 'react';
import { shallowEqual } from '@devexpress/dx-core';
import {
  PLUGIN_HOST_CONTEXT, RERENDER_TEMPLATE_EVENT,
  TEMPLATE_HOST_CONTEXT, RERENDER_TEMPLATE_SCOPE_EVENT,
} from './constants';
import { withContext } from '../utils/with-props-from-context';
import { PluginHostContext, TemplateHostContext, TemplateHostInterface } from './contexts';
import { PluginContextProps } from './plugin-context-prop-types';
import { TemplateBase } from './template';

export interface TemplatePlaceholderProps {
  /** The name of a template to be rendered. */
  name?: string;
  /** An object passed to the related template. */
  params?: object;
  children?: (content) => any;
}
interface TemplateHostContextProps {
  [TEMPLATE_HOST_CONTEXT: string]: TemplateHostInterface;
}

type Props = TemplatePlaceholderProps & PluginContextProps & TemplateHostContextProps;

const getRenderingData = (props: Props): { params?: object, templates: TemplateBase[] } => {
  const { name, params } = props;
  if (name) {
    const { [PLUGIN_HOST_CONTEXT]: pluginHost } = props;
    return {
      params,
      templates: pluginHost.collect(`${name}Template`)
        .filter(template => template.predicate(params))
        .reverse(),
    };
  }
  const { [TEMPLATE_HOST_CONTEXT]: templateHost } = props;
  return {
    params: params || templateHost.params(),
    templates: templateHost.templates(),
  };
};

class TemplatePlaceholderBase extends React.Component<Props> {
  subscription = {
    [RERENDER_TEMPLATE_EVENT]: (id: number) => {
      if (this.template && this.template.id === id) {
        this.forceUpdate();
      }
    },
    [RERENDER_TEMPLATE_SCOPE_EVENT]: (name: string) => {
      const { name: propsName } = this.props;
      if (propsName === name) {
        this.forceUpdate();
      }
    },
  };
  template: TemplateBase | null = null;
  params?: object = {};

  componentDidMount() {
    const { [PLUGIN_HOST_CONTEXT]: pluginHost } = this.props;
    pluginHost.registerSubscription(this.subscription);
  }

  shouldComponentUpdate(nextProps: Props) {
    const { params, templates } = getRenderingData(nextProps);
    const { children } = this.props;
    const [template] = templates;
    return children !== nextProps.children || this.template !== template
      || !shallowEqual(this.params, params);
  }

  componentWillUnmount() {
    const { [PLUGIN_HOST_CONTEXT]: pluginHost } = this.props;
    pluginHost.unregisterSubscription(this.subscription);
  }

  render() {
    const { params, templates } = getRenderingData(this.props);

    this.params = params;
    [this.template] = templates;
    const restTemplates = templates.slice(1);

    let content: ((...args) => any) | null = null;
    if (this.template) {
      const { children: templateContent } = this.template;

      content = templateContent() || null;
      if (content && typeof content === 'function') {
        content = content(params);
      }
    }

    const { children: templatePlaceholder } = this.props;
    return (
      <TemplateHostContext.Provider
        value={{
          templates: () => restTemplates,
          params: () => this.params,
        }}
      >
        {templatePlaceholder ? templatePlaceholder(content) : content}
      </TemplateHostContext.Provider>
    );
  }
}

/** A React component to which a related Template is rendered. */
export const TemplatePlaceholder: React.ComponentType<TemplatePlaceholderProps> = withContext(
  PluginHostContext, PLUGIN_HOST_CONTEXT,
)(
  withContext(TemplateHostContext, TEMPLATE_HOST_CONTEXT)(TemplatePlaceholderBase),
);
