export const Template: React.ComponentType<TemplateProps> = withHostAndPosition(TemplateBase);
import * as React from 'react';
import { UPDATE_CONNECTION_EVENT } from './constants';
import {
  isTrackedDependenciesChanged,
  getAvailableGetters,
  getAvailableActions,
} from './helpers';
import { PluginHostContext } from './contexts';
import { Getters, Actions } from '../types';

export interface TemplateConnectorProps {
  /** A function that renders a markup using Getters and Actions passed as arguments. */
  children: (
    getters: Getters,
    actions: Actions,
  ) => React.ReactNode;
}

/** @internal */
class TemplateConnectorBase extends React.Component<TemplateConnectorProps> {
  trackedDependencies: { [key: string]: { get: (args) => any }};
  subscription: { [UPDATE_CONNECTION_EVENT: string]: (args) => void };

  constructor(props, context) {
    super(props, context);

    this.trackedDependencies = {};
    this.subscription = {
      [UPDATE_CONNECTION_EVENT]: () => this.updateConnection(),
    };
  }

  componentDidMount() {
    const pluginHost = this.context;
    pluginHost.registerSubscription(this.subscription);
  }

  componentWillUnmount() {
    const pluginHost = this.context;
    pluginHost.unregisterSubscription(this.subscription);
  }

  updateConnection() {
    const pluginHost = this.context;

    if (isTrackedDependenciesChanged(pluginHost, this.trackedDependencies)) {
      this.forceUpdate();
    }
  }

  render() {
    const pluginHost = this.context;
    const { children } = this.props;

    const { getters, trackedDependencies } = getAvailableGetters(pluginHost);
    this.trackedDependencies = trackedDependencies;
    const actions = getAvailableActions(pluginHost);

    return children(getters, actions);
  }
}

TemplateConnectorBase.contextType = PluginHostContext;

/** A React component that provides access to Getters and Actions within a Template. */
export const TemplateConnector: React.ComponentType<TemplateConnectorProps> = TemplateConnectorBase;
const TIMEOUT = 180;

/** @internal */
export class TouchStrategy {
  delegate: any;
  touchStartTimeout: any;
  dragging: boolean;
  mouseInitialOffset: null;

  constructor(delegate) {
    this.delegate = delegate;
    this.touchStartTimeout = null;
    this.dragging = false;
  }

  isDragging() {
    return this.dragging;
  }

  isWaiting() {
    return !!this.touchStartTimeout;
  }

  cancelWaiting() {
    clearTimeout(this.touchStartTimeout);
    this.touchStartTimeout = undefined;
  }

  start(e) {
    const { clientX: x, clientY: y } = e.touches[0];
    this.touchStartTimeout = setTimeout(() => {
      this.delegate.onStart({ x, y });
      this.dragging = true;
    }, TIMEOUT);
  }

  move(e) {
    this.cancelWaiting();
    if (this.dragging) {
      const { clientX, clientY } = e.touches[0];
      e.preventDefault();
      this.delegate.onMove({ x: clientX, y: clientY });
    }
  }

  end(e) {
    this.cancelWaiting();
    if (this.dragging) {
      const { clientX, clientY } = e.changedTouches[0];
      this.delegate.onEnd({ x: clientX, y: clientY });
    }
    this.mouseInitialOffset = null;
    this.dragging = false;
  }
}
/* globals document:true */

let gestureCover: HTMLElement;
/** @internal */
export const toggleGestureCover = (toggle, cursor?) => {
  let style: React.CSSProperties = {
    pointerEvents: toggle ? 'all' : 'none',
  };
  if (toggle && cursor) {
    style = {
      ...style,
      cursor,
    };
  }
  if (!gestureCover) {
    style = {
      ...style,
      position: 'fixed',
      top: 0,
      right: 0,
      left: 0,
      bottom: 0,
      opacity: 0,
      zIndex: 2147483647,
    };

    gestureCover = document.createElement('div');
    document.body.appendChild(gestureCover);
  }
  Object.keys(style).forEach((key) => { gestureCover.style[key] = style[key]; });
};
/* globals document:true window:true */

/** @internal */
export const clear = () => {
  const selection = window.getSelection && window.getSelection();

  if (selection) {
    if (selection.empty) {
      selection.empty();
    } else if (selection.removeAllRanges) {
      selection.removeAllRanges();
    }
  }
};
