import React from 'react';
import {StoreSelector, ClientSelector} from './MenuSelector';
import {Socket} from './connector';


const App = socket => {
	var stores = [];
	var clients = [];

	Socket.on( 'PRODUCT_TABLE', data => {
		stores = data;
		console.log("stores")
		return (
			<div id="App">
				<StoreSelector items={stores}/>
				<ClientSelector items={clients}/>
			</div>
		);
	});
	Socket.on( 'STORE_ADDRESSES', data => {
		clients = data;
		return (
			<div id="App">
				<StoreSelector items={stores}/>
				<ClientSelector items={clients}/>
			</div>
		);
	});

	return (
		<div id="App">
			<StoreSelector items={stores}/>
			<ClientSelector items={clients}/>
		</div>
	);
}

export default App;
