import React from 'react';
import { ComboBox } from '@progress/kendo-react-dropdowns';
import {TransactionTable} from './Tables';
import io from 'socket.io-client';

import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import '@progress/kendo-theme-default/dist/all.css';

const url = "http://localhost:8000"

const socket = io( url )

function fixDate( date ) {
	if( date === undefined ) return undefined;
	let myDate = date.getFullYear() + "-"
	var month = (date.getMonth() + 1)
	if( month < 10 ) myDate += "0";
	myDate += month + "-"
	var day = date.getDate();
	if( day < 10 ) myDate += "0";
	myDate += day
	return myDate
}

const TransactionSelector = () => {
	const [state, setState] = React.useState({
		store: undefined, 
		payment_method: undefined,
		date: undefined,
		category: undefined,
		transaction_data: [], 
		possible_payment_methods: [ '', 'Credit Card', 'Cash' ],
		names: [],
	});
	const handleName = (event) => {
		setState({ ...state, store: event.target.value })
	}
	const handlePayment = (event) => {
		switch( event.target.value ) {
			case 'Credit Card':
				state.payment_method = 0
				break
			case 'Cash':
				state.payment_method = 1
				break
			default:
				state.payment_method = undefined
		}
		socket.emit('FETCH_TRANSACTIONS', ( { 'store': state.store, 
			'payment_method': state.payment_method,
			'Date_Time': fixDate( state.date ), 
			'category': state.category } ) )
	}
	const handleDate = (date) => {
		state.date = date
		socket.emit('FETCH_TRANSACTIONS', ( { 'store': state.store, 
			'payment_method': state.payment_method,
			'Date_Time': fixDate( state.date ), 
			'category': state.category } ) )
	}
	socket.on('TRANSACTION_DATA', data => {
		setState({ ...state, transaction_data: data })	
	});
	socket.on('STORE_ADDRESSES', data => {
		setState({ ...state, names: data })	
	});
	return (
		<div className="Selector">
		<h2> Επιλογή Καταστήματος </h2>
		<ComboBox data={state.names} onChange={handleName}/>
		<ComboBox data={state.possible_payment_methods} onChange={handlePayment}/>
		<DatePicker selected={state.date} onChange={handleDate}/>
		<TransactionTable data={state.transaction_data}/>
		</div>
	);
}

class ClientSelector extends React.Component {
	constructor( props ) {
		super( props )
		this.socket = io( url )
		this.item = undefined
		this.names = []
		this.customer_transactions = []
		this.customer_data = []
	}
	componentDidMount() {
		console.log( "Called componentDidMount" )
		this.socket.on('CUSTOMER_NAMES', data => {
			this.names = data
		});
		this.socket.on('CUSTOMER_DATA', data => {
			this.customer_data = Object.values( data )[ 0 ]
			this.socket.emit( 'FETCH_CUSTOMER_TRANSACTIONS', this.customer_data[ 'Card_Number' ] )
		});
		this.socket.on('CUSTOMER_TRANSACTIONS', data => {
			this.customer_transactions = data
			console.log( data )
		});

	}
	handleName( event ) {
		this.item = event.target.value
		this.socket.emit( 'SELECT_CUSTOMER', event.target.value )
	}
	render() {
		return (
			<div className="Selector">
			<h1> Επιλογή Πελάτη </h1>
			<ComboBox data={this.names} onChange={this.handleName}/>
			<TransactionTable data={this.customer_transactions}/>
			</div>
		);
	}
}

