import React from 'react';
import {StoreSelector, ClientSelector} from './MenuSelector';

const pelates = [ 'Loni', 'Potis' ]
const stores = [ 'superloni', 'superloni2', 'superpotis', 'mpouloukosAE' ]

const url = "http://localhost:8000"

const App = () => {
	function callAPI() {
		fetch( url )
		.then( res => res.text() )
	}
	return (
		<div id="App">
			<StoreSelector items={stores}/>
			<ClientSelector items={pelates}/>
		</div>
	);
}

export default App;
