import React, { Component } from 'react';
import './App.css';
import { DropDownList } from '@progress/kendo-react-dropdowns';
import '@progress/kendo-theme-default/dist/all.css'

class StoreSelector extends Component {
    constructor( props ) {
        super( props )
        this.state = {
            data: [ "Superloni", "Superpotis" ],
            selected: false,
            select_store: undefined
        }
    }
    handleChange( e ) {
        this.state.selected = true
        this.state.selected_store = e.target.value
    }
    render() {
        return (
            <div className="StoreSelector">
            <h1> Επιλογή Καταστήματος </h1>
            <DropDownList 
            data={this.state.data} onChange={this.handleChange}
            />            
            </div>
        );
    }
}

export default StoreSelector;
