import React from 'react';
import {StoreSelector, ClientSelector} from './MenuSelector';
import { connect } from 'react-redux';
import {selectCustomer, 
	updateCustomers, 
	updateStores, 
	updateCustomersData,
	getAllTransactions} from '../actions';
import io from 'socket.io-client'

const url = "http://localhost:8000"

const App = ({
	addresses,
	names,
	transactions
}) => {
	const manage_socket = () => {
		let socket = io( url )
		socket.on('CUSTOMER_NAMES', data => {
			names = data;
		});
		socket.on('STORE_ADDRESSES', data => {
			addresses = data;
		});
	};
	return (
		<div id="App">
			<StoreSelector items={addresses} grid_data={transactions}/>
			<ClientSelector items={names} grid_data={[]}/>
		</div>
	);
}

