import { initialCustomersState } from './initialState';

const customersReducer = (state = initialCustomersState, action ) => {
	switch( action.type ) {
		case 'UPDATE_CUSTOMERS':
			return Object.freeze({
				...state,
				items: [
					...action.payload
				]
			});
		default: return state;
	}
	return state;
}

export {customersReducer};
